import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme: any) => {
  return {
    polkaSmithContainer: {
      maxWidth: 1280,
      alignItems: "center",
      padding: 60,
      fontFamily: 'DM Sans',
      margin: "auto"
    },
    headerContainer: {
      display: "flex",
      width: "100%",
    },
    headerText: {
      display: "inline-flex",
      width: "50%"
    },
    headerImg: {
      display: "inline-flex",
      width: "50%"
    },
    headerText1: {
      fontSize: 48,

    }
  };
});

export default useStyles;
