import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme: any) => {
  return {
    polkaSmithMain: {
      //backgroundColor: "#020618",
      width: "100%",
      height: "auto",
      paddingTop: 20,
      minHeight: 640
    },
    polkaSmithContainer: {
      maxWidth: 1280,
      alignItems: "center",
      padding: 60,
      fontFamily: 'DM Sans',
      margin: "auto",
      color: "white",
    },
    headerContainer: {
      display: "flex",
      width: "100%",
    },
    headerText: {
      display: "inline-block",
      width: "40%"
    },
    headerImg: {
      display: "inline-block",
      width: "60%",
      paddingLeft: 50
    },
    headerText1: {
      fontSize: 48,
    },
    headerContent :{
      marginTop: 20,
      fontSize: 16,
      lineHeight: 1.5,
      color: "#AEAEAE"
    },
    overviewLabel: {
      width: "100%",
      marginTop: 60,
      display: "flex",
      borderWidth: 1,
      borderStyle: "solid",
      borderColor: "#ffffff36"
    },
     label: {
      width: "25%",
       padding: 20,
       lineHeight: 1.8,
       borderRight: "inherit"
     },
    introContainer: {
      marginTop: 100,
      background: "linear-gradient(red, yellow)",
      height:100,
      width: "100%",
      borderRadius: 10
    }
  };
});

export default useStyles;
