import {withRouter} from 'react-router-dom';
import useStyles from './style';
import withWidth from '@material-ui/core/withWidth';
import DefaultLayout from '../../components/Layout/DefaultLayout';

const iconSearch = 'images/icons/search.svg';
const headerImg = '/images/polkasmith/header_img.png';

const JoinPolkaSmith = (props: any) => {
  const styles = useStyles();
  return (
      <DefaultLayout>
        <div className={styles.polkaSmithMain}>
          <div className={styles.polkaSmithContainer}>
            <div className={styles.headerContainer}>
              <div className={styles.headerText}>
                <p><span className={styles.headerText1}>
          <b>Join Polka</b>Smith<b> on Kusama Parachain Auction</b>
        </span></p>
                <p className={styles.headerContent}>
                  PolkaSmith is the canary network of PolkaFoundry on Kusama. PolkaSmith’s native token is PKS, which we
                  migrated from PolkaFoundry’s PKF at a 1:1 ratio.
                </p>
                <p className={styles.headerContent}>
                  Participants who support PolkaSmith to win the Kusama auction will earn worthy rewards in PKS. In
                  addition, there are an extra 5% PKS for participants on Red Kite, 0.5% for referrals, and 10% for
                  early birds.
                </p>
              </div>
              <div className={styles.headerImg}>
                  <img src={headerImg}/>
              </div>
            </div>
          </div>
        </div>
      </DefaultLayout>
  );
};

export default withWidth()(withRouter(JoinPolkaSmith));
